import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './EntretienObjectifs.css';

const API_URL = 'http://localhost:3002/api';

const CATEGORIES = {
  'competences': { label: 'Compétences', icon: '🎯', color: '#2196F3' },
  'performance': { label: 'Performance', icon: '📈', color: '#4CAF50' },
  'developpement': { label: 'Développement', icon: '🌱', color: '#FF9800' },
  'projets': { label: 'Projets', icon: '📋', color: '#9C27B0' },
  'comportemental': { label: 'Comportemental', icon: '🤝', color: '#00BCD4' },
  'autre': { label: 'Autre', icon: '📌', color: '#607D8B' }
};

const PRIORITES = {
  'basse': { label: 'Basse', color: '#9E9E9E' },
  'moyenne': { label: 'Moyenne', color: '#FF9800' },
  'haute': { label: 'Haute', color: '#f44336' }
};

const STATUTS = {
  'en_cours': { label: 'En cours', icon: '⏳', color: '#2196F3' },
  'atteint': { label: 'Atteint', icon: '✅', color: '#4CAF50' },
  'non_atteint': { label: 'Non atteint', icon: '❌', color: '#f44336' },
  'reporte': { label: 'Reporté', icon: '⏸️', color: '#FF9800' }
};

function EntretienObjectifs() {
  const { id } = useParams();
  const [entretien, setEntretien] = useState(null);
  const [objectifs, setObjectifs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingObjectif, setEditingObjectif] = useState(null);
  const [filterCategorie, setFilterCategorie] = useState('all');
  const [formData, setFormData] = useState({
    titre: '',
    description: '',
    categorie: 'competences',
    priorite: 'moyenne',
    date_echeance: '',
    statut: 'en_cours',
    progres: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    loadData();
  }, [id]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [entretienRes, objectifsRes] = await Promise.all([
        axios.get(`${API_URL}/entretiens/${id}`),
        axios.get(`${API_URL}/entretiens/${id}/objectifs`)
      ]);
      
      setEntretien(entretienRes.data);
      setObjectifs(objectifsRes.data);
    } catch (error) {
      console.error('Erreur chargement données:', error);
      alert('Erreur lors du chargement');
      navigate('/entretiens');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editingObjectif) {
        const response = await axios.put(`${API_URL}/objectifs/${editingObjectif.id}`, formData);
        setObjectifs(objectifs.map(obj => obj.id === editingObjectif.id ? response.data : obj));
        alert('Objectif modifié avec succès');
      } else {
        const response = await axios.post(`${API_URL}/entretiens/${id}/objectifs`, formData);
        setObjectifs([...objectifs, response.data]);
        alert('Objectif ajouté avec succès');
      }

      resetForm();
    } catch (error) {
      console.error('Erreur sauvegarde objectif:', error);
      alert('Erreur lors de la sauvegarde');
    }
  };

  const handleEdit = (objectif) => {
    setEditingObjectif(objectif);
    setFormData({
      titre: objectif.titre,
      description: objectif.description || '',
      categorie: objectif.categorie,
      priorite: objectif.priorite,
      date_echeance: objectif.date_echeance ? objectif.date_echeance.split('T')[0] : '',
      statut: objectif.statut,
      progres: objectif.progres
    });
    setShowAddForm(true);
  };

  const handleDelete = async (objectifId) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cet objectif ?')) {
      return;
    }

    try {
      await axios.delete(`${API_URL}/objectifs/${objectifId}`);
      setObjectifs(objectifs.filter(obj => obj.id !== objectifId));
      alert('Objectif supprimé avec succès');
    } catch (error) {
      console.error('Erreur suppression:', error);
      alert('Erreur lors de la suppression');
    }
  };

  const resetForm = () => {
    setShowAddForm(false);
    setEditingObjectif(null);
    setFormData({
      titre: '',
      description: '',
      categorie: 'competences',
      priorite: 'moyenne',
      date_echeance: '',
      statut: 'en_cours',
      progres: 0
    });
  };

  const filteredObjectifs = filterCategorie === 'all' 
    ? objectifs 
    : objectifs.filter(obj => obj.categorie === filterCategorie);

  const objectifsByCategorie = Object.keys(CATEGORIES).reduce((acc, cat) => {
    acc[cat] = filteredObjectifs.filter(obj => obj.categorie === cat);
    return acc;
  }, {});

  if (loading) {
    return <div className="loading">Chargement...</div>;
  }

  if (!entretien) {
    return <div className="loading">Entretien introuvable</div>;
  }

  return (
    <div className="objectifs-page">
      <header className="objectifs-header">
        <div className="header-left">
          <button onClick={() => navigate('/entretiens')} className="back-btn">
            ← Retour
          </button>
          <div className="header-info">
            <h1>🎯 Objectifs - {entretien.titre}</h1>
            <div className="entretien-meta">
              <span>👤 {entretien.employee?.nom}</span>
              <span>📅 {new Date(entretien.date_prevue).toLocaleDateString('fr-FR')}</span>
            </div>
          </div>
        </div>
        <button 
          onClick={() => setShowAddForm(!showAddForm)}
          className="add-objectif-btn"
        >
          {showAddForm ? '❌ Annuler' : '➕ Nouvel objectif'}
        </button>
      </header>

      <div className="objectifs-stats">
        <div className="stat">
          <span className="stat-number">{objectifs.length}</span>
          <span className="stat-label">Total</span>
        </div>
        <div className="stat">
          <span className="stat-number">{objectifs.filter(o => o.statut === 'en_cours').length}</span>
          <span className="stat-label">En cours</span>
        </div>
        <div className="stat">
          <span className="stat-number">{objectifs.filter(o => o.statut === 'atteint').length}</span>
          <span className="stat-label">Atteints</span>
        </div>
        <div className="stat">
          <span className="stat-number">
            {objectifs.length > 0 
              ? Math.round((objectifs.reduce((sum, o) => sum + o.progres, 0) / objectifs.length)) 
              : 0}%
          </span>
          <span className="stat-label">Progression moyenne</span>
        </div>
      </div>

      {showAddForm && (
        <div className="form-section">
          <h3>{editingObjectif ? 'Modifier l\'objectif' : 'Nouvel objectif'}</h3>
          <form onSubmit={handleSubmit} className="objectif-form">
            <div className="form-row">
              <div className="form-group full">
                <label>Titre de l'objectif *</label>
                <input
                  type="text"
                  value={formData.titre}
                  onChange={(e) => setFormData({...formData, titre: e.target.value})}
                  placeholder="Ex: Améliorer les compétences en gestion de projet"
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Catégorie *</label>
                <select
                  value={formData.categorie}
                  onChange={(e) => setFormData({...formData, categorie: e.target.value})}
                  required
                >
                  {Object.entries(CATEGORIES).map(([key, val]) => (
                    <option key={key} value={key}>
                      {val.icon} {val.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Priorité *</label>
                <select
                  value={formData.priorite}
                  onChange={(e) => setFormData({...formData, priorite: e.target.value})}
                  required
                >
                  {Object.entries(PRIORITES).map(([key, val]) => (
                    <option key={key} value={key}>{val.label}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Date d'échéance</label>
                <input
                  type="date"
                  value={formData.date_echeance}
                  onChange={(e) => setFormData({...formData, date_echeance: e.target.value})}
                />
              </div>
            </div>

            {editingObjectif && (
              <div className="form-row">
                <div className="form-group">
                  <label>Statut</label>
                  <select
                    value={formData.statut}
                    onChange={(e) => setFormData({...formData, statut: e.target.value})}
                  >
                    {Object.entries(STATUTS).map(([key, val]) => (
                      <option key={key} value={key}>
                        {val.icon} {val.label}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="form-group">
                  <label>Progression ({formData.progres}%)</label>
                  <input
                    type="range"
                    min="0"
                    max="100"
                    value={formData.progres}
                    onChange={(e) => setFormData({...formData, progres: parseInt(e.target.value)})}
                  />
                </div>
              </div>
            )}

            <div className="form-row">
              <div className="form-group full">
                <label>Description</label>
                <textarea
                  value={formData.description}
                  onChange={(e) => setFormData({...formData, description: e.target.value})}
                  placeholder="Détails et contexte de l'objectif..."
                  rows="4"
                />
              </div>
            </div>

            <div className="form-actions">
              <button type="submit" className="save-btn">
                {editingObjectif ? '💾 Sauvegarder' : '➕ Ajouter'}
              </button>
              <button type="button" onClick={resetForm} className="cancel-btn">
                Annuler
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="filters">
        <button 
          className={`filter-btn ${filterCategorie === 'all' ? 'active' : ''}`}
          onClick={() => setFilterCategorie('all')}
        >
          Toutes ({objectifs.length})
        </button>
        {Object.entries(CATEGORIES).map(([key, val]) => {
          const count = objectifs.filter(o => o.categorie === key).length;
          return count > 0 ? (
            <button
              key={key}
              className={`filter-btn ${filterCategorie === key ? 'active' : ''}`}
              onClick={() => setFilterCategorie(key)}
            >
              {val.icon} {val.label} ({count})
            </button>
          ) : null;
        })}
      </div>

      <div className="objectifs-container">
        {filteredObjectifs.length === 0 ? (
          <div className="empty-state">
            <p>Aucun objectif dans cette catégorie</p>
          </div>
        ) : (
          filterCategorie === 'all' ? (
            Object.entries(objectifsByCategorie).map(([cat, objs]) => 
              objs.length > 0 && (
                <div key={cat} className="categorie-section">
                  <h3 className="categorie-title" style={{ borderLeftColor: CATEGORIES[cat].color }}>
                    {CATEGORIES[cat].icon} {CATEGORIES[cat].label} ({objs.length})
                  </h3>
                  <div className="objectifs-grid">
                    {objs.map(obj => (
                      <ObjectifCard 
                        key={obj.id} 
                        objectif={obj} 
                        onEdit={handleEdit}
                        onDelete={handleDelete}
                      />
                    ))}
                  </div>
                </div>
              )
            )
          ) : (
            <div className="objectifs-grid">
              {filteredObjectifs.map(obj => (
                <ObjectifCard 
                  key={obj.id} 
                  objectif={obj} 
                  onEdit={handleEdit}
                  onDelete={handleDelete}
                />
              ))}
            </div>
          )
        )}
      </div>
    </div>
  );
}

function ObjectifCard({ objectif, onEdit, onDelete }) {
  const categorie = CATEGORIES[objectif.categorie];
  const priorite = PRIORITES[objectif.priorite];
  const statut = STATUTS[objectif.statut];

  return (
    <div className="objectif-card">
      <div className="objectif-header">
        <div className="objectif-badges">
          <span className="badge-categorie" style={{ backgroundColor: categorie.color }}>
            {categorie.icon} {categorie.label}
          </span>
          <span className="badge-priorite" style={{ backgroundColor: priorite.color }}>
            {priorite.label}
          </span>
        </div>
        <div className="objectif-actions">
          <button onClick={() => onEdit(objectif)} className="edit-btn" title="Modifier">
            ✏️
          </button>
          <button onClick={() => onDelete(objectif.id)} className="delete-btn" title="Supprimer">
            🗑️
          </button>
        </div>
      </div>

      <h4 className="objectif-titre">{objectif.titre}</h4>

      {objectif.description && (
        <p className="objectif-description">{objectif.description}</p>
      )}

      <div className="objectif-progress">
        <div className="progress-header">
          <span>Progression</span>
          <span className="progress-value">{objectif.progres}%</span>
        </div>
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${objectif.progres}%` }}
          />
        </div>
      </div>

      <div className="objectif-footer">
        <span className="objectif-statut" style={{ color: statut.color }}>
          {statut.icon} {statut.label}
        </span>
        {objectif.date_echeance && (
          <span className="objectif-date">
            📅 {new Date(objectif.date_echeance).toLocaleDateString('fr-FR')}
          </span>
        )}
      </div>
    </div>
  );
}

export default EntretienObjectifs;
